class LinearModel{constructor(){this.isFit=!1}}class LinearRegression extends LinearModel{constructor(){super(),this.m=0,this.b=0}fit(t,r){for(var s=0,h=0,i=0,e=0,n=0;n<t.length;n++)s+=t[n],h+=r[n],i+=t[n]*r[n],e+=t[n]*t[n];this.m=(t.length*i-s*h)/(t.length*e-Math.pow(Math.abs(s),2)),this.b=(h*e-s*i)/(t.length*e-Math.pow(Math.abs(s),2)),this.isFit=!0}predict(t){var r=[];if(this.isFit)for(var s=0;s<t.length;s++)r.push(this.m*t[s]+this.b);return r}mserror(t,r){for(var s=0,h=0;h<t.length;h++)s+=Math.pow(t[h]-r[h],2);return s/t.length}}function joinArrays(){var t=[];if(6==arguments.length){t.push([arguments[0],arguments[2],arguments[4]]);for(var r=0;r<arguments[1].length;r++)t.push([arguments[1][r],arguments[3][r],arguments[5][r]])}return t}